import "./App.css";

function App(){

  const [count, setCount] = useState(0);
  return(
    <div className="App">
    <header>
      <h1>Counter app using State/Hooks</h1>
    </header>
    <h2>Current value of Count is {count}</h2>
    {/* <button onClick={() => setCount(0)}>Reset Counter</button>
