    <button onClick={() => setCount(0)}>Reset Counter</button>
    <button onClick={() => (count>=10)?"":setCount(count + 1)}>Increase Counter</button>
    <button onClick= {() => setCount(count - 1)}>Decrease Counter</button>
    </div>
  )
}

export default App;
